let currentDifficulty = "";
let currentQuestionIndex = 0;
let currentQuestion = null;

window.onload = function() {
    const quizArea = document.getElementById('quiz-area');
    quizArea.style.display = 'none';
};

function selectDifficulty(difficulty) {
    currentDifficulty = difficulty;
    const difficultySelection = document.getElementById('difficulty-selection');
    if (difficultySelection) {
        difficultySelection.style.display = 'none !important';
    }

    currentQuestion = null;
    displayQuestion();
    document.getElementById('quiz-area').style.display = 'block';
}

function displayQuestion() {
    const quizArea = document.getElementById("quiz-area");
    if (!currentDifficulty) {
        quizArea.innerHTML = "<p>난이도를 선택해주세요.</p>";
        return;
    }

    let questions;
    switch (currentDifficulty) {
        case "easy":
            questions = easyQuestions;
            break;
        case "medium":
            questions = mediumQuestions;
            break;
        case "hard":
            questions = hardQuestions;
            break;
        default:
            return;
    }

    if (questions.length === 0) {
        quizArea.innerHTML = "<p>문제가 더이상 없습니다!</p>";
    }

    const randomIndex = Math.floor(Math.random() * questions.length);
    currentQuestion = questions[randomIndex];

    questions.splice(randomIndex, 1);

    quizArea.innerHTML = `
        <div class="question">${currentQuestion.question}</div>
        <div class="options">
            <button onclick="checkAnswer('O')">O</button>
            <button onclick="checkAnswer('X')">X</button>
        </div>
        <div id="result"></div>
    `;
}

function checkAnswer(answer) {
    const resultDiv = document.getElementById("result");
    const explanation = currentQuestion.explanation;
    
    // Remove existing result elements
    while (resultDiv.firstChild) {
        resultDiv.removeChild(resultDiv.firstChild);
    }

    const resultMessage = document.createElement('p');
    if (answer === currentQuestion.answer) {
        resultMessage.textContent = "정답입니다!";
    } else {
        resultMessage.textContent = `오답입니다! 정답은 ${currentQuestion.answer}입니다.`;
    }
    const explanationMessage = document.createElement('p');
    explanationMessage.textContent = explanation;
    const nextQuestionButton = document.createElement('button');
    nextQuestionButton.textContent = "다음 질문";
    nextQuestionButton.onclick = nextQuestion;    
    nextQuestionButton.classList.add('o-button');
    resultDiv.appendChild(resultMessage);
    resultDiv.appendChild(explanationMessage);
    resultDiv.appendChild(nextQuestionButton);
}

function nextQuestion() {
    currentQuestionIndex++;
     displayQuestion();
    }